(function () {
    function relocate() {
        var url = this.getAttribute("data-url");
        url += encodeURIComponent(this.value);
        if (this.parentNode.parentNode.id.substr(0, 18) === "uploader_container") {
            url += "#" + this.parentNode.parentNode.id;
        }
        location.href = url;
    };

    var ids = ["uploader-type", "uploader-subdir", "uploader-resize"];
    for (var i = 0; i < ids.length; i++) {
        var element = document.getElementById(ids[i]);
        if (element) {
            element.onchange = relocate;
        }
    }
}());
