jQuery(function ($) {
    $(".uploader_widget").each(function () {
        var element = this;

        $(".uploader_type, .uploader_subdir, .uploader_resize", element).change(function () {
            location.href = $(this).data("url").replace("FIXME", encodeURIComponent(this.value));
        });

        var config = $(element).data("config");
        config.container = $(".uploader_buttons", element).get(0);
        config.browse_button = $(".uploader_pickfiles", element).get(0);
        config.drop_element = element;
        config.init = ({
            PostInit: function (uploader) {
                $(".uploader_filelist", element).html("");

                $(".uploader_uploadfiles", element).click(function () {
                    uploader.start();
                    return false;
                });
            },
            FilesAdded: function (up, files) {
                $.each(files, function () {
                    $(".uploader_filelist", element).append(
                        $("<div>", {id: this.id})
                            .text(this.name + " (" + plupload.formatSize(this.size) + ") ")
                            .append("<b>")
                    );
                });
            },
            UploadProgress: function (up, file) {
                $("#" + file.id).find("b").text(file.percent + "%");
            },
            Error: function (up, err) {
                $(".uploader_console", element).text("Error #" + err.code + ": " + err.message + "\n");
            }
        });

        new plupload.Uploader(config).init();
    });
});
